mapper.select:
        SELECT * FROM lottery_board

	        WHERE lottery_name LIKE CONCAT('%', #{lotteryName}, '%')


	        AND purchase_date >= #{startDate}


	        AND purchase_date <= #{endDate}


	        ORDER BY id DESC
	        LIMIT #{offset}, #{pageSize}

        SELECT COUNT(*) FROM lottery_board

	        WHERE lottery_name LIKE CONCAT('%', #{lotteryName}, '%')


	        AND purchase_date >= #{startDate}


	        AND purchase_date <= #{endDate}
